# Code
```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:

 TreeNode* exe(vector<int> &nums, int i , int j){
    if(i>j)
    return NULL;
int mid=(i+j)/2;
   TreeNode* root= new TreeNode(nums[mid]); 
root->left=exe(nums,i,mid-1);
root->right=exe(nums,mid+1,j);

return root;
}

 vector<int> inorder(vector<int>& nums,TreeNode* root,int key) {
       if(root==NULL)
           return nums;
      inorder(nums,root->left,key);
      if(root->val !=key)
      nums.push_back(root->val);
      inorder(nums,root->right,key);

      return nums;     
    } 
 TreeNode* deleteNode(TreeNode* root, int key) {
  if(root==NULL)
  return NULL;
     vector<int> nums;
     inorder(nums,root,key);
         return exe(nums,0,nums.size()-1);
    }
};
```

