class Solution {
public:
    TreeNode* minNode(TreeNode* root){
        while(root->left!=NULL){
            root = root->left;
        }
        return root;
    }

    TreeNode* deleteNode(TreeNode* root, int key) {
        if(root==NULL){
//             if node not found then return NULL
            return NULL;
        }

        if(root->val < key){
//             search for the node in right subtree
            root->right = deleteNode(root->right, key);
        }
        else if(root->val > key){
//             search for the node in left subtree
            root->left = deleteNode(root->left, key);
        }
        else{
//             if node is found


//     Case 1:         if root is the leaf node
            if(root->left==NULL && root->right==NULL){
                root=NULL;
            }
//     Case 2.1:         if root has only right child
            else if(root->left==NULL){
                root = root->right;
            }
//     Case 2.2:         if root has only left child
            else if(root->right==NULL){
                root = root->left;
            }
//     Case 3:     if root has both children
            else{
                TreeNode* Node;
//                 find min node in right subtree of current(root) node
                Node = minNode(root->right);
//                 copy value of that node into root node
                root->val = Node->val;
//                 delete that minNode found in right subtree
                root->right = deleteNode(root->right,Node->val);
            }
        }
        return root;
    }
};
```

