# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def deleteNode(self, root: Optional[TreeNode], key: int) -> Optional[TreeNode]:
        def get_minimum_val(node):
            while node.left:
                node = node.left
            return node.val

        def get_maximum_val(node):
            while node.right:
                node = node.right
            return node.val

        def rec(node, key):
            if not node:
                return None

            if key < node.val:
                node.left = rec(node.left, key)
                return node

            if node.val < key:
                node.right = rec(node.right, key)
                return node

            if node.right:
                succ_val = get_minimum_val(node.right)
                node.val = succ_val
                node.right = rec(node.right, succ_val)
                return node
            elif node.left:
                pred_val = get_maximum_val(node.left)
                node.val = pred_val
                node.left = rec(node.left, pred_val)
                return node
            else:
                return None

        return rec(root, key)
