```
public TreeNode deleteNode(TreeNode root, int key) {
	if(root==null) return null; //if root is null just return null
	if(root.val==key) //if current node is to be deleted
	{
		if(root.right==null) return root.left; //there is no right subtree
												//simply return left subtree

		TreeNode runner = root.right;         //right subtree exists
		TreeNode attach = root.right; //keep a pointer to right subtree, will need it

		//find leftmost node of right subtree
		//why? all values in left subtree are smaller than the
		//smallest value in the right subtree

		while(runner.left!=null) 
			runner=runner.left;  //finding smallest node in right subtree

		runner.left = root.left;  //attach left subtree to smallest node in right
		return attach;              //return the right node of deleted node
	}
	else{
		//key not found here? check for it in left and right subtree
		//since its a BST we can choose in which subtree to look 
        if (root.val > key) root.left = deleteNode(root.left, key); 
        else root.right = deleteNode(root.right, key);
		return  root;
	}
}
}
