class Solution {
public:
    TreeNode* deleteNode(TreeNode* root, int key) {
        if(!root) return nullptr;
        if(key > root->val) {
            root->right = deleteNode(root->right, key);
        } else if(key < root->val) {
            root->left= deleteNode(root->left, key);
        } else {
            // the node has only one child. delete the node and return its child
            if(!root->left || !root->right) {
                TreeNode* ret = !root->left ? root->right : root->left;
                delete root;
                return ret;
            }
            // the node to be deleted has two children
            TreeNode* to_delete = root;
            root = rightMin(root->right);
            root->right= delRightMin(to_delete->right);
            root->left = to_delete->left;
            delete to_delete;
