class Solution {
public:
  TreeNode* deleteNode( TreeNode* root, int key ) {
    if ( !root ) {
      return root;
    }

    TreeNode dummy( 0 );
    dummy.left     = root;
    TreeNode *head = root, *prev = &dummy;
    bool      bleft = true;
    while ( head && head->val != key ) {
      prev = head;
      head = ( bleft = key < head->val ) ? head->left : head->right;
    }
    if ( !head ) {
      return root;  // not found
    }

    if ( head->right ) {
      // head has right child; use the two-pointer method to find successor
      TreeNode *slow = head->right, *fast = slow->left;
      if ( fast ) {
        while ( fast->left ) {
          slow = slow->left;
          fast = fast->left;
        }
        // fast is the successor of head; slow is fast's parent
        head->val  = fast->val;
        slow->left = fast->right;
        delete fast;
      } else {
        // slow is already the successor of head
        head->val   = slow->val;
        head->right = slow->right;
        delete slow;
      }
    } else {
      // head has no right child
      TreeNode*& prevchild = bleft ? prev->left : prev->right;
      prevchild            = head->left;
      delete head;
    }
    return dummy.left;
  }
};
