    DFSino(node.left);
    node.val !== key && arr.push(node);
    DFSino(node.right);
  }
  function build(nodes) {
    if(!nodes.length) return null
    const idx = Math.floor(nodes.length / 2);
    nodes[idx].left = build(nodes.slice(0, idx));
    nodes[idx].right = build(nodes.slice(idx + 1));
    return nodes[idx];
  }
};
