 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution
{
public:
	TreeNode *deleteNode(TreeNode *root, int key)
	{
		TreeNode *node = new TreeNode(key);

		return delete1(root, node);
	}
	TreeNode *delete1(TreeNode *root, TreeNode *node)
	{
		if (root == NULL)
		{
			//  return s;
			return root;
		}
		TreeNode *temp = root, *prev = temp;
		while (temp != NULL)
		{
			if (node->val < temp->val)
			{
				prev = temp;
				temp = temp->left;
			}
			else if (node->val > temp->val)
			{
				prev = temp;
				temp = temp->right;
			}
			else if (temp->val == node->val)
			{
				if (temp->left == temp->right)
				{
					if (prev == temp)
					{
						root = NULL;
					}
					else
					{
						prev->right == temp ? prev->right = NULL : prev->left = NULL;
					}
					break;
				}
				else if (temp->left == NULL)
				{
					if (temp == prev)
					{
						root = temp->right;
					}
					else
					{
						prev->right == temp ? prev->right = temp->right : prev->left = temp->right;
					}
				}
				else if (temp->right == NULL)
				{
					if (temp == prev)
					{
						root = temp->left;
					}
					else
					{
						prev->right == temp ? prev->right = temp->left : prev->left = temp->left;
					}
				}
				else
				{
					TreeNode *t = temp->right, *pre = temp;
					while (t->left)
					{
						pre = t;
						t = t->left;
					}
					if (pre == temp)
					{
						if (prev == temp)
						{
							t->left = temp->left;
							root = t;
						}
						else
						{
							prev->left == temp ? prev->left = t : prev->right = t;
							t->left = pre->left;
						}
					}
					else
					{
						if (prev == temp)
						{
							t->left = temp->left;
							pre->left = t->right;
							t->right = temp->right;
							root = t;
						}
						else
						{
							prev->right == temp ? prev->right = t : prev->left = t;
							pre->left = t->right;
							t->left = temp->left;
							t->right = temp->right;
						}
					}
				}
				break;
			}
		}
		return root;
	}
};

