        //base case
        if(root==NULL)
        return NULL;
        if(key<root->val)
       root->left= deleteNode(root->left,key);

        else if(key>root->val)
        root->right=deleteNode(root->right,key);
        else
        {
            //four cases arise when key==root->info ist only left child presentt 2 right child only present 3 both presentm4 mot bothpresent
            if(root->left==NULL && root->right==NULL)
            {
                delete root;
                return NULL;
            }
            if(root->left==NULL && root->right!=NULL)
            {
                TreeNode * temp=root->right;
                delete root;
                return temp;

