var frequencySort = function(s) {
  const frequency = s.split('').reduce((obj, val) => {
    obj[val] = obj[val] + 1 || 1;
    return obj
  }, {});
  return Object.entries(frequency)
    .sort(([_, fx], [__, fy]) => fy - fx)
    .map(([char, freq]) => char.repeat(freq))
    .join('');
};
