class Solution {

    class Alphabet {
        Character character;
        Integer count;
    }

    public String frequencySort(String s) {
        if(s == null || s.length() == 0){
            return s;
        }
        Map<Character, Alphabet> map = new HashMap<>();
        for(char c : s.toCharArray()) {
            if(map.containsKey(c)){
                map.get(c).count++;
            } else {
                Alphabet alphabet = new Alphabet();
                alphabet.character = c;
                alphabet.count = 1;
                map.put(c, alphabet);
            }
        }

        List<Alphabet> alphabets = new ArrayList<>(map.values());

        Collections.sort(alphabets, new Comparator<Alphabet>(){
            @Override
            public int compare(Alphabet o1, Alphabet o2) {
                return o2.count.compareTo(o1.count);
            }
        });

        StringBuilder result = new StringBuilder("");
        for(int i = 0; i < alphabets.size(); i++){
            Alphabet a = alphabets.get(i);
            for(int j = 0; j < a.count; j++){
                result.append(a.character);
            }
        }
        return result.toString();
    }
}
```

