Steps to proceed:
	 1. First count the frequency of each unique characters.
	 2. Define a structure of frequency and corresponding character.
	 3. Sort the structure.
	 4. Store the sorted characters in the resultant string.


class Solution {

	//defining a structure
    struct d{
        int no;
        char r;
    };

	//sorting the structure
    static bool comp(d a, d b){
        return (a.no > b.no);
    }

public:
    string frequencySort(string s) {

        int n=s.size();
        sort(s.begin(),s.end());
        int f[n];
        int c=1;
        int j=0;

		//calculating the frequency of unique characters
        for(int i=1;i<n;i++){
            if(s[i]==s[i-1]){
                c++;
            }
            else{
                f[j]=c;
                c=1;
                j++;
            }
        }
        f[j]=c;

        int k=0;
        j++;

		//defining array of structure
        d arr[j];

		//storing the values in the array
        for(int i=0;i<j;i++){
            arr[i].no=f[i];
            arr[i].r=s[k];
            k=k+f[i];
        }

		//sorting the array of structure
        sort(arr,arr+j,comp);

		//the resultant string
        string res="";
        for(int i=0;i<j;i++){
            while(arr[i].no){
                res+=arr[i].r;
                arr[i].no--;
            }
        }
        return res;
    }
};

