         map.put(c,map.getOrDefault(c,0) + 1);
     }

    PriorityQueue<Character> maxHeap = new PriorityQueue<>((a,b) -> map.get(b)-map.get(a));
    maxHeap.addAll(map.keySet());

    StringBuilder result = new StringBuilder();
