import "strings"
import "sort"

type Fr struct {
    Ch rune
    Fr int
}

type Frs []Fr

func (this Frs) Len() int { return len(this) }

func (this Frs) Less(a, b int) bool { return this[a].Fr > this[b].Fr }

func (this Frs) Swap(a, b int) { this[a], this[b] = this[b], this[a] }

func frequencySort(s string) string {
    a, b := make([]Fr, 256), strings.Builder{}

    for _, v := range s {
        a[v].Ch = v
        a[v].Fr++
    }

    sort.Sort(Frs(a))

    for _, v := range a {
        b.WriteString(strings.Repeat(string(v.Ch), v.Fr))
    }

    return b.String()
}
