class Solution {
public:
    string frequencySort(string s) {
	// There are 255 characters in the ASCII table. The first character is static_cast<char>(0)
        vector<pair<char, int>> ourMapping(255, {static_cast<char>(0), 0});
		// Here I'm making a frequency count of the entire ascii table. The frequency of
		// each letter starts at 0.
        for (int i{}; i < 255; ++i) {
            ourMapping[i].first += i;
        }
        // Updating the frequency count of the mapping I just made.
        for (int i{}; i < s.size(); ++i) {
            ourMapping[s[i] - static_cast<char>(0)].second++;
        }
        // Sorting the ASCII table by frequency.
		// Most frequent characters first in the vector. For example, if you have "Abbb"
		// <b, 3> <A, 1> <ANOTHER LETTER, 0> <ANOTHER LETTER, 0>...
        sort(ourMapping.begin(), ourMapping.end(), [](const pair<char, int>& A, const pair<char, int>& B){
            return A.second > B.second;
        });

        string returnStr;

      // Recreating the ascii table frequency vector in string form.
        for (int i{}; i < ourMapping.size(); ++i) {
            if (ourMapping[i].second) {
			// string constructor overload is used to create a string of uniform characters.
			// For instance, string(4, '*') creates "****", string(2, 'A') created string "AA" 
                returnStr += string(ourMapping[i].second, ourMapping[i].first);
            }
        }

        return returnStr;
    }
};

