class Solution:
    def frequencySort(self, s: str) -> str:
        count = collections.Counter(s)
        k = len(count)
        res = ''
        for ch in heapq.nsmallest(k, count, key = lambda ch: (~count[ch], ch)):
            res += ch * count[ch]
        return res
