class Solution {
public:
    string frequencySort(string s) {
    map<char,int>mp; string res;
    multimap<int, char>mm;
    for(auto ch:s) mp[ch]++;
    for(auto &ch:mp) mm.insert({ch.second,ch.first});
    for(auto &i:mm) res.insert(res.length(), i.first, i.second);
    reverse(res.begin(), res.end());
    return res;
    }
};
