class Solution {
    func frequencySort(_ s: String) -> String {
        Dictionary(s.map{($0, 1)}, uniquingKeysWith:+) // create [Character:Int]; keys=ch, values=count
			.sorted{ a,b in a.value > b.value }  // sort key-value pairs by ascending count
			.reduce("") { result, tuple in
				let (ch, count) = tuple
				return result + String(repeating: ch, count: count)
			}
    }
}
