void CreateTable(int*arr)
{
    for(int i=0;i<62;i++)
        arr[i]=0;
}


void FillTable(char* s,int* arr)
{
    for(int i=0;i<strlen(s);i++)
    {
        char ch=s[i];
        int j=(int)ch;
        if(j>=48 && j<=57)
        {
            j=j-48;
            arr[j]=arr[j]+1;
        }
        else if(j>=65 && j<=90)
        {
            j=j-55;
            arr[j]=arr[j]+1;
        }
        else if(j>=97 && j<=122)
        {
            j=j-61;
            arr[j]=arr[j]+1;
        }
    }
}


void SortTable(int* arr,struct HashMap** sarr)
{
    for(int i=0;i<62;i++)
    {
        int maxindex=-1;
        int max=-1;
        for(int j=0;j<62;j++)
        {
            if(arr[j]>=max)
            {
                max=arr[j];
                maxindex=j;
            }
        }
        arr[maxindex]=-2;
        struct HashMap* node=(struct HashMap*)malloc(sizeof(struct HashMap));
        node->index=maxindex;
        node->frequency=max;
        sarr[i]=node;
    }
}

