class Solution:
    def totalNQueens(self, n: int) -> int:
        def backtrack(i):
            if i == n:
                return 1
            res = 0
            for j in range(n):
                if j not in cols and i-j not in diag and i+j not in off_diag:
                    cols.add(j)
                    diag.add(i-j)
                    off_diag.add(i+j)
                    res += backtrack(i+1)
                    off_diag.remove(i+j)
                    diag.remove(i-j)
                    cols.remove(j)
            return res

        cols = set()
        diag = set()
        off_diag = set()
        return backtrack(0)
