            c[col] = f[i] = b[j] = true;
            ans += bt(c, f, b, row + 1, n);
            c[col] = f[i] = b[j] = false;
        }
        return ans;
    }

    public int totalNQueens(int n) {
        return bt(new boolean[n], new boolean[2 * n], new boolean[2 * n], 0, n);
