class Solution {

    static int[][] chess;
    public int totalNQueens(int n) {
        chess = new int[n][n];
        return solveNQueensHelper(n, 0);
    }
    public static int solveNQueensHelper(int n, int currLevel) {
        if(currLevel == n){
            return 1;
        }
        int ways = 0;
        for(int i = 0; i < n; i++){
            if(isSafeForQueen(currLevel, i)){
                chess[currLevel][i] = 1;
                ways += solveNQueensHelper(n, currLevel+1);
                chess[currLevel][i] = 0;
            }
        }
        return ways;
    }

    public static boolean isSafeForQueen(int r, int c) { 
        //col
        for(int i = r-1; i >= 0; i--){
            if(chess[i][c] == 1) return false;
        }
        //right dia
        for(int i = r-1, j = c+1; i >= 0 && j < chess.length; i--, j++){
            if(chess[i][j] == 1) return false;
        }
        //left dia
        for(int i = r-1, j = c-1; i >= 0 && j >= 0; i--, j--){
            if(chess[i][j] == 1) return false;
        }
        return true;
    }

}
```

