        int x = i -1;
        while(x>=0){
            if(vis[x][j]){
                return false;
            }
            x--;
        }
        x = i -1;
        int y= j -1;
        while(x>=0 && y>=0){
            if(vis[x][y]){
                return false;
            }
            x--;y--;
        }
        x = i-1;
        y = j+1;
        while(x>=0 && y<n){
            if(vis[x][y]){
                return false;
            }
            x--;y++;
        }
        return true;
    }

    void countNQ(int r, int n){
        if(r == n){
            cnt++;
            return;
        }

        for(int c= 0; c<n; c++){
            if(isvalid(r, c,n)){
                vis[r][c] = 1;
                countNQ(r+1, n);
                vis[r][c] = 0;
            }
        }
    }

    int totalNQueens(int n) {
        // vector<vector<int>>vis(n, vector<int>(n));
        countNQ(0,n);
        return cnt;
    }
