class Solution {
public:
    int totalNQueens(int n) {

        int aCol[n + 1];

        return countQueens(n, aCol, 0); 
    }

    int countQueens(int n, int *pCol, int pIndex) {

        if (pIndex >= n) { return 1; }

        int aCount = 0;

        for (int i=0;i<n;i++) {

            bool aBlocked = false;

            for (int aBack=pIndex-1;aBack>=0;aBack--) {

                if (pCol[aBack] == i) { aBlocked = true; }

                int aDiff = pCol[aBack] - i;
                if (aDiff < 0) { aDiff = -aDiff; }

                if ((pIndex - aBack) == aDiff) {
                    aBlocked = true;
                }
            }
            if (aBlocked) { continue; }

            pCol[pIndex] = i;

            aCount += countQueens(n, pCol, pIndex + 1);   
        }
        return aCount;
