class Solution {
public:
    int solve(int n, int row, int colMask, int leftDiagMask, int rightDiagMask) {
        if (row == n) {
            return 1;
        }
        int ans = 0;
        int rowState = (colMask | leftDiagMask | rightDiagMask) & ((1 << n) - 1);
        int x = rowState ^ ((1 << n) - 1);
        while (x) {
            int queenPos = x & (-x);
            x -= queenPos;
            if (!(queenPos & rowState)) {
                ans += solve(n, row + 1, colMask | queenPos, (leftDiagMask | queenPos) << 1, (rightDiagMask | queenPos) >> 1);
            }
        }
        return ans;
    }
    int totalNQueens(int n) {
        return solve(n, 0, 0, 0, 0);
    }
};
