class Solution {
public:
bool isSafe(vector<string> &board, int row, int col, vector<int> &lowerDiagonal, vector<int> &upperDiagonal, vector<int>&leftSideRow, int n) {
        if(upperDiagonal[n-1+col-row]!=1 && leftSideRow[row]!=1 && lowerDiagonal[row+col]!=1) return true;
        return false;
    }

    void backtrack(int col, vector<string>&board, int&ans, int n, vector<int>&lowerDiagonal, vector<int>&upperDiagonal, vector<int>& leftSideRow) {
        if(col==n) {
            ans++;
            return;
        }
        for(int i=0; i<n; i++) {
            if(isSafe(board, i, col, lowerDiagonal, upperDiagonal, leftSideRow, n)) {
                board[i][col] = 'Q';
                lowerDiagonal[i+col] = 1;
                upperDiagonal[n-1+col-i] = 1;
                leftSideRow[i] = 1;
                backtrack(col+1, board, ans, n, lowerDiagonal, upperDiagonal, leftSideRow);
                board[i][col] = '.';
                lowerDiagonal[i+col] = 0;
                upperDiagonal[n-1+col-i] = 0;
                leftSideRow[i] = 0;
            }
        }
    }
    int totalNQueens(int n) {
        int ans = 0;
