int maxProduct(int A[], int n) {
    if(n == 0){
        return 0;
    }
    int* maxdp = new int[n];
    int* mindp = new int[n];
    maxdp[0] = A[0];
    mindp[0] = A[0];
    int maxProduct = A[0];
    for(int i = 1; i < n; i++){
        if(A[i] > 0){
            maxdp[i] = max(maxdp[i - 1] * A[i], A[i]);
            mindp[i] = min(mindp[i - 1] * A[i], A[i]);
        }
        else{
            maxdp[i] = max(mindp[i - 1] * A[i], A[i]);
            mindp[i] = min(maxdp[i - 1] * A[i], A[i]);
        }
        maxProduct = max(maxProduct, maxdp[i]);
    }
    return maxProduct;
}

