        for x in nums[1:]:
            if x<0:
                tmp=curr_max
                curr_max=curr_min
                curr_min=tmp
            curr_max=max(curr_max*x, x)
            curr_min=min(curr_min*x, x)

            max_p=max(max_p, curr_max)
        return max_p
