            lower->second.end = max(lower->second.end, val);
        } else if (higher != m.end() && higher->first == val + 1) {
            // [4, 5] with 3
            m[val] = {val, higher->second.end};
            m.erase(higher);
        } else {
            m[val] = {val, val};
