class SummaryRanges {
public:
    //The numberline 
    vector<bool> arr;
    int mx = -1;
    SummaryRanges() {
        arr = vector<bool>(10001, false);
    }

    void addNum(int value) {
        arr[value] = true;
        mx = max(value, mx);
    }

    vector<vector<int>> getIntervals() {
        vector<vector<int>> ans;
        // finding ranges
        for(int i = 0; i <= mx; i++){
            int j = i;
            while(j <= mx && arr[j]){
                j++;
            }
            // check if the current range is valid or not
            if(arr[i])
            ans.push_back(vector<int>{i,j-1});
            i = j;
        }
        return ans;
    }
    // Note: we don't need to check for 10^4 everytime,
    // we used mx to check until maximum element.
};
```

