// ***************************Binary Search***************************

class SummaryRanges {
    vector<vector<int>> intervals;
public:
    /** Initialize your data structure here. */
    SummaryRanges() {

    }

    void addNum(int value) {
        int l = 0, r = intervals.size() - 1;
        while (l <= r) {
            int mid = (l + r) / 2;
            if (intervals[mid][0] <= value && intervals[mid][1] >= value) return;
            if (intervals[mid][1] < value) l = mid + 1;
            else r = mid - 1;
        }
        if (l == 0) {
            if (intervals.size() > 0 && intervals[0][0] == value + 1) {
                intervals[0][0] = value;
                return;
            }
        }
        else if (l == intervals.size()) {
            if (intervals[l - 1][1] == value - 1) {
                intervals[l - 1][1] = value;
                return;
            }
        }
        else {
            if (intervals[l - 1][1] == value - 1 && intervals[l][0] == value + 1) {
                intervals[l - 1][1] = intervals[l][1];
                intervals.erase(intervals.begin() + l);
                return;
            }
            if (intervals[l - 1][1] == value - 1) {
                intervals[l - 1][1] = value;
                return;
            }
            if (intervals[l][0] == value + 1) {
                intervals[l][0] = value;
                return;
            }
        }
        intervals.insert(intervals.begin() + l, vector<int>{value, value});
    }

    vector<vector<int>> getIntervals() {
        return intervals;
    }
};



