class SummaryRanges:

    def __init__(self):
        self.intervals = []

    def _merge_intervals(self, interval):
        if len(self.intervals) == 0:
            self.intervals.append(interval)
            return

        intervals = []
        i = 0
        while i < len(self.intervals):
            if interval[0] <= self.intervals[i][1]+1:
                break
            intervals.append(self.intervals[i])

            i+=1

        def merge_single_intervals(interval1, interval2):
            return [min(interval1[0], interval2[0]), max(interval1[1], interval2[1])]

        merged_interval = interval

        while i<len(self.intervals):
            if self.intervals[i][0] <= merged_interval[1]+1:
                merged_interval = merge_single_intervals(self.intervals[i], merged_interval)
            else:
                break

            i+=1
        intervals.append(merged_interval)

        while i<len(self.intervals):
            intervals.append(self.intervals[i])
            i+=1

        self.intervals = intervals

    def addNum(self, value: int) -> None:
        self._merge_intervals([value, value])


    def getIntervals(self) -> List[List[int]]:
        return self.intervals

