func (sr *SummaryRanges) AddNum(val int)  {
	l, r := 0, len(sr.ranges)-1
	for l <= r {
		m := (l+r)>>1
		if val >= sr.ranges[m][0] && val <= sr.ranges[m][1] {
			// in a valid range, nothing to do
