        vector<vector<int>> ans;
        if(pq.empty()) return ans;
        priority_queue<int, vector<int>, greater<int>> copy = pq; 
        while(!copy.empty()) {
            vector<int> temp;
            int first = copy.top();
            copy.pop();
            temp.push_back(first);
            first++;
            while(!copy.empty() and copy.top() == first) {
                first++;
                copy.pop();
            }
            temp.push_back(first-1);
            ans.push_back(temp);
        }
        return ans;
    }
};

