private void addValueToRange(int value, int index) {
    int[] interval = intervals.get(index);
    int lowRange = interval[0], highRange = interval[1];
    if (index > 0 && value == lowRange - 1 && intervals.get(index - 1)[1] + 1 >= value) {// ex. 3-3 5-6 [insert 4] - merge with left one
        intervals.get(index - 1)[1] = highRange;
        intervals.remove(index);
        return;
    } else if (index + 1 < intervals.size() && value == highRange + 1 && intervals.get(index + 1)[0] - 1 <= value) { // ex. 3-3 [insert 4] 5-6 - merge with right
        intervals.get(index)[1] = intervals.get(index + 1)[1];
        intervals.remove(index + 1);
        return;
    }
    if (value == lowRange - 1) intervals.get(index)[0] = value;
    else if (value == highRange + 1) intervals.get(index)[1] = value;
}
