class SummaryRanges {

    TreeMap<Integer, Integer> tm = new TreeMap<>();
    public SummaryRanges() {
    }

    public void addNum(int val) {        
        Integer prev = tm.lowerKey(val);
        Integer next = tm.higherKey(val);

        tm.put(val, val);

        if (next != null && next == val + 1) {
            tm.put(val, tm.get(next));
            tm.remove(next);
        }
        if (prev != null && tm.get(prev) >= val - 1) {
            tm.put(prev, Math.max(tm.get(prev), tm.get(val)));
            tm.remove(val);
        } 
    }

    public int[][] getIntervals() {
        int[][] ans = new int[tm.size()][2];
        int idx = 0;
        for (Map.Entry<Integer, Integer> e : tm.entrySet()) {
            ans[idx++] = new int[]{e.getKey(), e.getValue()};
        }
        return ans;
    }
}
