class Range implements Comparable<Range>{
    public int start, end;

    public Range(int x, int y){
        this.start = x;
        this.end = y;
    }

    @Override
    public int compareTo(Range other){
        return this.start - other.start;
    }
}

class SummaryRanges {
    private TreeSet<Range> set;

    /** Initialize your data structure here. */
    public SummaryRanges() {
        this.set = new TreeSet<Range>();
    }

    public void addNum(int val) {
        Range current = new Range(val, val);

        Range before = this.set.floor(current);
        Range after = this.set.ceiling(current);

        if(before != null && before.end + 1 >= val){
            current.start = before.start;
            current.end = Math.max(val, before.end);
            this.set.remove(before);
        }

        if(after != null && after.start == val + 1){
            current.end = after.end;
            this.set.remove(after);
        }

        this.set.add(current);
    }

    public int[][] getIntervals() {
        int N = this.set.size();
        int[][] result = new int[N][2];
        int i = 0;

        for(Range range : this.set){
            result[i][0] = range.start;
            result[i][1] = range.end;
            ++i;
        }

        return result;
    }
}

