    def __init__(self):
        """
        Initialize your data structure here.
        """
        self.data = []

    def addNum(self, val: int) -> None:
        if(not self.data):
            self.data.append([val, val])
            return

        if(val == self.data[0][0]-1):
            self.data[0][0] = val
            return
        if(val < self.data[0][0]):
            self.data.insert(0, [val, val])
            return

        i = 0
        L = len(self.data)
        while(i<L and self.data[i][0] <= val):
            i += 1
        if(i == L):
            if(self.data[i-1][1] >= val):
                pass
            elif(self.data[i-1][1]+1 == val):
                self.data[i-1][1] =val
            else:
                self.data.append([val, val])
        else:
            if(self.data[i-1][1] + 1 == val):
                self.data[i-1][1] = val
                if(self.data[i-1][1]+1 == self.data[i][0]):
                    self.data[i-1][1] = self.data[i][1]
                    self.data.pop(i)
            elif(self.data[i-1][1] >= val):
                pass
            elif(self.data[i][0]-1 == val):
                self.data[i][0] = val
            else:
                self.data = self.data[:i] + [[val, val]] + self.data[i:]

    def getIntervals(self) -> List[List[int]]:
        return self.data

