        Integer higherKey = map.higherKey(val);

        if (lowerKey != null && higherKey != null && map.get(lowerKey) + 1 == val && higherKey - 1 == val) {
            map.put(lowerKey, map.get(higherKey));
            map.remove(higherKey);
