// When do we merge the number to an existing interval?
// 1. If it's within 1 interval(No merge operation needed in this case)
// 2. If it's 1 less than left bound or 1 larger than right bound of 1 interval

// How do we check this?
// Java TreeSet + Overridden Comparator
// O(log n) where n is the number of intervals

class SummaryRanges {
    TreeSet<Range> tree;

    /** Initialize your data structure here. */
    public SummaryRanges() {
        tree = new TreeSet<>(new Comparator<Range>() {
            @Override
            public int compare(Range o1, Range o2) {
                if(o1.start > o2.end) {
                    return 1;
                } else if(o1.end < o2.start) {
                    return -1;
                } else {
                    return 0; // overlap
                }
            }
        });

    }

    public void addNum(int val) {
        Range newRange = new Range(val, val);
        if(!tree.contains(newRange)) {
            tree.add(newRange);
            Range lower = tree.lower(newRange);
            Range higher = tree.higher(newRange);
            if(lower != null && lower.end == newRange.start - 1) {
                tree.remove(lower);
                newRange.start = lower.start;
            }
            if(higher != null && higher.start == newRange.end + 1) {
                tree.remove(higher);
                newRange.end = higher.end;
            }
        }
    }

    public int[][] getIntervals() {
        int size = tree.size();
        int[][] intervals = new int[size][];
        if(tree.isEmpty()) {
            return intervals;
        }
        Range cur = tree.first();
        for(int i = 0 ; i < size; i++) {
            intervals[i] = new int[2];
            intervals[i][0] = cur.start;
            intervals[i][1] = cur.end;
            cur = tree.higher(cur);
        }
        return intervals;
    }


    private class Range {
        int start;
        int end;

        Range(int start, int end) {
            this.start = start;
            this.end = end;
        }
    }
}
