protected:
    set<int> order;
public:
    /** Initialize your data structure here. */
    SummaryRanges() {

    }
    // O(log(N)) to add
    void addNum(int val) {
        order.insert(val);
    }
    // O(N) ot get interval
    vector<vector<int>> getIntervals() {
        vector<vector<int>> ret;
        if(order.empty()) return ret;
        auto it = (order.begin());
        int start = *it;
        int expect = start+1;
        it++;
        while(it!=order.end()){
            if(*it!=expect){
                ret.push_back({start,expect-1});
                start = *it;
                expect = start+1;
            }
            else expect++;
            it++;
        }
		// dont forget to push the last one
        ret.push_back({start,expect-1});
        return ret;
    }
};
