class SummaryRanges {
public:

    vector<int> parent;
    vector<int> size;
    int mx = 0;
    SummaryRanges() {
      parent.resize(10001, -1);
      size.resize(10001, 1);
       mx = 0;
    }

    void make(int val)
    {
        if(parent[val]==-1)
        parent[val] = val;
    }

    int findpar(int a)
    {
        if(parent[a]==a)
        return a;
        return parent[a] = findpar(parent[a]);
    }

    void Union(int a, int b)
    {
        a = findpar(a);
        b = findpar(b);
        parent[b] = a;
        size[a] += size[b];
    }

    void addNum(int value) {
         mx = max(value, mx);
         make(value);
         if(value-1>=0 && parent[value-1]!=-1)
         {
             if(findpar(value)!=findpar(value-1))
             Union(value-1, value);
         }
         if(value+1<=10000 && parent[value+1]!=-1)
         {
             if(findpar(value)!=findpar(value+1))
             Union(value, value+1);
         }
    }

    vector<vector<int>> getIntervals() {
           vector<vector<int>> ans;
           for(int i = 0; i<=mx; i++)
           {
               if(parent[i]==i)
               {
                  vector<int> v = {i, i+size[i]-1};
                  ans.push_back(v);
               }
           }
           return ans;
    }
};

