    if (num === min - 1) {
      middleInterval[0] =
        intervals[middleIndex - 1]?.[1] === num - 1
          ? intervals.splice(middleIndex - 1, 1)[0][0]
          : num;
    }
  }

  // O(1)
  public getIntervals(): number[][] {
    return this.intervals;
  }
}
