class SummaryRanges {
    int head[];
    int max;
    public SummaryRanges() {
        head = new int[10002];
        Arrays.fill(head, -1);
        max = 0;
    }

    public void addNum(int val) {
        max = Math.max(max, val);
        if(val == 0)
        head[0] = 0;
        else {
            int prev = findHead(val-1);
            if(prev != -1) {
                head[val] = prev;
            }
            else {
                head[val] = val;
            }
        }

        if(head[val+1] != -1) {
            head[val+1] = head[val];
        }
    }

    public int[][] getIntervals() {
        int n = countSets();
        int res[][] = new int[n][2];
        findSets(res, n);
        return res;
    }

    private int findHead(int x) {
        if(head[x] == -1) {
            return -1;
        }
        if(head[x] == x) {
            return x;
        }
        return findHead(head[x]);
    }

    private int countSets() {
        int i = max;
        int count = 0;
        while(i >= 0) {
            int x = findHead(i);
            if(x == -1) {
                i--;
            }
            else if(x == i) {
                count++;
                i--;
            }
            else {
                count++;
                i = x-1;
            }
        }

        return count;
    }

    private void findSets(int res[][], int n) {
        int i = max;
        int index = n-1;

        while(i >= 0) {
            int x = findHead(i);
            if(x == -1) {
                i--;
            }
            else if(x == i) {
                res[index--] = new int[]{x, x};
                i--;
            }
            else {
                res[index--] = new int[]{x, i};
                i = x-1;
            }
        }
    }
}

