type SummaryRanges struct {
	val [][]int
}

func Constructor() SummaryRanges {
	return SummaryRanges{
		val: [][]int{},
	}
}

func (this *SummaryRanges) AddNum(value int) {
	low, high := 0, len(this.val)-1
	if high == -1 {
		this.val = [][]int{{value, value}}
		return
	}

	for low <= high {
		median := (low + high) / 2

		if this.val[median][0] <= value && value <= this.val[median][1] {
			return
		}

		if this.val[median][1] == value-1 {
			this.val[median][1] = value
			if median != len(this.val)-1 {
				if this.val[median][1]+1 == this.val[median+1][0] {
					this.val[median+1][0] = this.val[median][0]
					this.val = append(this.val[:median], this.val[median+1:]...)
				}
			}
			return
		}

		if this.val[median][0] == value+1 {
			this.val[median][0] = value
			if median != 0 {
				if this.val[median][0]-1 == this.val[median-1][1] {
					this.val[median-1][1] = this.val[median][1]
					this.val = append(this.val[:median], this.val[median+1:]...)
				}
			}
			return
		}

		if this.val[median][1] < value {
			low = median + 1
		} else {
			high = median - 1
		}
	}

	this.val = append(this.val[:low], append([][]int{{value, value}}, this.val[low:]...)...)
}

func (this *SummaryRanges) GetIntervals() [][]int {
	return this.val
}
