```
public class SummaryRanges {
    private readonly SortedSet<(int start, int end)> _list = new(new Comparer());

    public void AddNum(int value)
    {
        if (_list.TryGetValue((value, value), out _))
            return;
        bool hasPrefix = _list.TryGetValue((value - 1, value - 1), out var prefix);
        bool hasSufix = _list.TryGetValue((value + 1, value + 1), out var sufix);

        if (hasPrefix)
            _list.Remove(prefix);
        if (hasSufix)
            _list.Remove(sufix);
        _list.Add((hasPrefix ? prefix.start : value, hasSufix ? sufix.end : value));
    }

    public int[][] GetIntervals() =>
        _list
        .Select(x => new int[] {x.start, x.end})
        .ToArray();

