class SummaryRanges {
public:

    vector<vector<int>> sry ;
    SummaryRanges() {

    }

    vector<vector<int>> adjust(vector<vector<int>>a, int x){
        // if(x>=a.size()-1||x=<0)return a ;
        if(x!=0&&a[x][0]-1==a[x-1][1]){
            a[x-1][1]=a[x][1];
            //remove a[x]
            a.erase(a.begin() + x);
            return a ; 
        }
        if(x!=a.size()-1&&a[x][1]+1==a[x+1][0]){
            a[x][1]=a[x+1][1];
            //remove a[x+1] 
            a.erase(a.begin() +(x+1));
            return a ;
        }
        return a ;
    }

    void addNum(int v) {
        if(sry.empty()){ sry.push_back({v, v});
        return ; }
        int newRange = 1 ;
        for(int i = 0 ; i < sry.size() ; i++){
            int s = sry[i][0], e = sry[i][1] ;
            if(s <= v && v <=e) return ; //within range
            if(s-1==v){
                newRange =0 ;
                sry[i][0] = v ;
                sry = adjust(sry,i);
                break;
            }
            if(e+1==v){
               newRange =0 ;
                sry[i][1] = v ;
                sry = adjust(sry,i);
                break; 
            }
        }
        if(newRange){ sry.push_back({v, v});
        return ; }
    }

    vector<vector<int>> getIntervals() {
        sort(sry.begin(), sry.end());
        return sry ;
    }
};

