public class SummaryRanges
{
    private class IntervalComparer : IComparer<int[]>
    {
        public int Compare(int[]? x, int[]? y)
        {
            return x[0].CompareTo(y[0]);
        }
    }

    private Dictionary<int, int[]> _numbers;
    private SortedSet<int[]> _intervals;

    public SummaryRanges()
    {
        _numbers = new Dictionary<int, int[]>();
        _intervals = new SortedSet<int[]>(new IntervalComparer());
    }

    public void AddNum(int value)
    {
        if (_numbers.ContainsKey(value)) return;

        int[] range;
        if(_numbers.ContainsKey(value - 1))
        {
            if (_numbers.ContainsKey(value + 1))
            {
                range = new int[] { _numbers[value - 1][0], _numbers[value + 1][1] };

                for (int i = _numbers[value - 1][0]; i < _numbers[value - 1][1]; i++)
                    _numbers[i] = range;
                for (int i = _numbers[value + 1][0] + 1; i <= _numbers[value + 1][1]; i++)
                    _numbers[i] = range;

                _intervals.Remove(_numbers[value - 1]);
                _intervals.Remove(_numbers[value + 1]);
                _intervals.Add(range);

                _numbers[value - 1] = range;
                _numbers[value + 1] = range;
            }
            else
            {
                _numbers[value - 1][1] = value;
                range = _numbers[value - 1];
            }
        }
        else if(_numbers.ContainsKey(value + 1))
        {
            _numbers[value + 1][0] = value;
            range = _numbers[value + 1];
        }
        else
        {
            range = new[] { value, value };
            _intervals.Add(range);
        }

        _numbers.Add(value, range);
    }

    public int[][] GetIntervals()
    {
        return _intervals.ToArray();
    }
}
