class SummaryRanges:

    def __init__(self):
		# list to store values
        self.rtr = []

    def addNum(self, value: int) -> None:
        if not self.rtr:
			# if list is empty add the value
            self.rtr.append(value)
        else:
			# if value is already present just return
            if value in self.rtr:
                return
            f=False
			# traverse the list to find insert position for current elm -> you can use bisect module to do the same in logn time complexity.
            for index,elm in enumerate(self.rtr):
                if elm > value:
                    f=True
                    break
            if f:
				# inserting the element to its position so that the list is sorted
                self.rtr.insert(index,value)
            else:
                self.rtr.insert(index+1,value)

    def getIntervals(self) -> List[List[int]]:
		# default interval we'll update it inside loop of O(n) time complexity
        interval = [-2,-2]
		# parent will store all intervals
        parent = []
		# traversing the sorted elements list
        for num in self.rtr:
			# if no interval is available -> change the default interval to create one
            if interval[0]==-2:
                interval[0] = num
                interval[1] = num
            else:
				# otherwise check if the gap b/w them is 1 if it is update the intervals end with current number
                if interval[-1] + 1 == num:
                    interval[-1] = num
				# otherwise append the current interval and create a new interval
                else:
                    parent.append(list(interval))
                    interval[0] = num
                    interval[1] = num
		# if last interval is not been added to final list add the last one
        if interval[0]!=-2:
            parent.append(interval)
        # print(self.rtr,parent)
        return parent

