class SummaryRanges {
public:

	set<int> st;

	SummaryRanges() {

	}

	void addNum(int value) 
	{
		st.insert(value);
	}

	vector<vector<int>> getIntervals() 
	{
		vector<vector<int>> res;
		auto it = st.begin();
		int start = *it;
		int end = *it;
		it++;
		for(; it != st.end(); it++)
		{
			if(end + 1 == *it)
			{
				end = *it;
				continue;
			}
			else
			{
				vector<int> temp;
				temp.push_back(start);
				temp.push_back(end);
				start = *it;
				end = *it;
				res.push_back(temp);
			}
		}

		vector<int> temp;
		temp.push_back(start);
		temp.push_back(end);
		res.push_back(temp);
		return res;
	}
};

